import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import {Bar} from 'react-chartjs-2'

const Game = props => (
    <tr>
      <td>{props.game.id}</td>
      <td>{props.game.result}</td>
      <td>{props.game.moves}</td>
      <td>{props.game.date}</td>
      <td>{props.game.time}</td>
      <td>{props.game.colour}</td>
      <td>
        <a href="#" onClick={() => { props.deleteGame(props.game._id) }}>delete</a>
      </td>
    </tr>
  )

const BarChart = () => {
    return <div>
        <Bar
            data = {{
                labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
                datasets : [{
                    label: '# of Votes',
                    data: this.state.games,
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)',
                        'rgba(75, 192, 192, 0.2)',
                        'rgba(153, 102, 255, 0.2)',
                        'rgba(255, 159, 64, 0.2)'
                    ],
                    borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)',
                        'rgba(255, 159, 64, 1)'
                    ],
                    borderWidth: 1
                }]
            }}
            height={400}
            width={600}
            options= {{
                maintainAspectRatio: false,
                scales: {
                    yAxes:[
                        {ticks: {
                            beginAtZero : true,
                        }
                        }
                    ]
                }
            }}
        />
    </div>

}  
export default class GamesList extends Component {
    constructor(props) {
        super(props);

        this.deleteGame = this.deleteGame.bind(this);

        this.state = {games: []};
    }

    componentDidMount() {
        axios.get('http://localhost:5000/Chess/')
            .then(response => {
                this.setState({ games: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    deleteGame(id) {
        axios.delete('http://localhost:5000/Chess/'+id)
          .then(response => { console.log(response.data)});
    
        this.setState({
          games: this.state.games.filter(el => el._id !== id)
        })
      }
    
    gameList() {
    return this.state.games.map(currentgame => {
        return <Game game={currentgame} deleteGame={this.deleteGame} key={currentgame._id}/>;
    })
    }

    drawGraph() {
        return BarChart()
    }

    render() {
        return (
            <div>
                <h3>Logged Games</h3>
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>ID</th>
                            <th>Result</th>
                            <th>Moves</th>
                            <th>Date</th>
                            <th>Time</th>
                            <th>Colour</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.gameList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
